mapper.select:
select ag.author_group_id as authorGroupId, ag.author_group_nm as authorGroupNm, ag.author_group_desc as authorGroupDesc, ag.sort_ordr as sortOrdr, ag.use_yn as useYn, ag.visit_yn as visitYn, ag.emp_yn as empYn, (select count(1) from author_group_detail_tb gd where gd.author_group_id = ag.author_group_id) as detailCnt from author_group_tb ag where 1=1
and ag.use_yn = #{useYn}
and ag.author_group_id = #{authorGroupId}
and ag.author_group_nm like concat('%', #{authorGroupNm}, '%')
and ag.author_group_id not in ( select gt.author_group_id from user_author_group_tb gt where gt.fuid = #{fuid} and gt.fpartcd1 = #{siteId} )
order by ag.sort_ordr

mapper.delete:
delete from user_author_group_tb
 where fuid = #{fuid}
   and fpartcd1 = #{siteId}

    	delete from user_author_group_tb
    	where fuid = #{fuid}
    	  and fpartcd1 = #{siteId}	

			and author_group_id not in

	    	 	#{grpCd}

delete from user_author_group_tb
 where fuid = #{fuid}
   and fpartcd1 = #{siteId}
   and author_group_id = #{authorGroupId}

delete from user_author_group_tb
 where fuid = #{fuid}
   and fpartcd1 = #{fpartcd1}

mapper.select:
select count(*)
from user_author_group_tb
where fuid = #{fuid}
  and fpartcd1 = #{siteId}
  and author_group_id = #{authorGroupId}

select fuid, fpartcd1 as siteId 
  from user_author_group_tb 
 where author_group_id = #{authorGroupId}
   and fpartcd1 = #{siteId}

mapper.update:
update author_group_detail_tb
   set use_yn = #{useYn}
     , modify_id = #{modifyId}		   
     , modify_dt = now(3)
 where author_group_id = #{authorGroupId}

mapper.insert:

    		select COALESCE(max(log_id),0)+1 from author_group_detail_log


