mapper.select:
SELECT id, building_id, area_id, floor_id, door_nm, alarm_typ, created_at, updated_at FROM T_DOOR TD WHERE 1=1
AND TD.building_id = #{building_id}
AND TD.area_id = #{area_id}
AND TD.floor_id = #{floor_id}

mapper.insert:
insert
  into
T_DOOR (   area_id
         , alarm_typ
         , door_nm
         , floor_id
         , created_at
         , updated_at
         )
 values  ( #{areaid}
		 , #{alarm_typ}
		 , #{door_nm}
		 , #{floor_id}
		 , SYSDATE
		 , SYSDATE
         )
