mapper.select:
SELECT e.id, f.emp_cd, face_state_typ, c1.cd_nm as face_state_typ_nm, face_feature_typ, c2.cd_nm as face_feature_typ_nm, e.error, e.created_at, emp.emp_nm, emp.dept_nm FROM T_FACE_FEATURE_ERR e JOIN T_FACE f on f.id = e.face_id LEFT OUTER JOIN T_EMP emp on f.emp_cd = emp.emp_cd LEFT OUTER JOIN T_CMMN_CD c1 on c1.cd = f.face_state_typ and c1.cd_typ = 'FaceStateTyp' LEFT OUTER JOIN T_CMMN_CD c2 on c2.cd = e.face_feature_typ and c2.cd_typ = 'FaceFeatureTyp' WHERE 1=1
AND f.emp_cd = #{emp_cd}
AND e.face_feature_typ = #{face_feature_typ}
AND emp.emp_nm LIKE CONCAT('%',#{emp_nm},'%')
order by e.created_at desc limit #{srchCnt} offset #{offset}

SELECT count(1) FROM T_FACE_FEATURE_ERR e JOIN T_FACE f on f.id = e.face_id WHERE 1=1
AND f.emp_cd = #{emp_cd}
AND e.face_feature_typ = #{face_feature_typ}
AND emp.emp_nm LIKE CONCAT('%',#{emp_nm},'%')
