mapper.select:
select id as id ,(SELECT cd_nm from T_CMMN_CD WHERE cd_typ = 'HolidayTyp' AND cd = holiday_typ) as holidayTypNm ,holiday_typ as holidayTyp ,holiday_nm as holidayNm ,holiday as holiday ,holiday_end as holidayEnd ,use_yn as useYn ,DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt ,DATE_FORMAT(updated_at, '%Y-%m-%d') as updatedAt from T_HOLIDAY where 1=1
and holiday_typ = #{srchCond}
and holiday_nm like CONCAT('%',#{keyword},'%')
and holiday between #{startDt} and #{endDt}
limit #{srchCnt} offset #{offset}

select count(*) from T_HOLIDAY where 1=1
and holiday_typ = #{srchCond}
and holiday_nm like CONCAT('%',#{keyword},'%')
and holiday between #{startDt} and #{endDt}
