mapper.select:
select eh.id, eh.evt_dt as evtDt, eh.ent_evt_typ as entEvtTyp, c1.cd_nm as entEvtTypNm, eh.terminal_cd as terminalCd, eh.emp_cd as empCd, emp_nm as empNm, eh.face_id as faceId, card_no as cardNo, card_class_typ as cardClassTyp, c2.cd_nm as cardClassTypNm, card_state_typ as cardStateTyp, c3.cd_nm as cardStateTypNm, card_tag_typ as cardTagTyp, c4.cd_nm as cardTagTypNm, beg_dt as begDt, end_dt as endDt, auth_way_typ as authWayTyp, c5.cd_nm as authWayTypNm, match_score as matchScore, face_threshold as faceThreshold, capture_at as captureAt, tag_at as tagAt, tag_card_no as tagCardNo, tag_emp_cd as tagEmpCd, temper as temper, mask_confidence as maskConfidence, tm.terminal_typ as terminalTyp, tm.door_cd as doorCd, door_nm as doorNm, building_cd as buildingCd, building_nm as buildingNm, dept_cd as deptCd, IFNULL(dept_nm,'') as deptNm , eh.created_at as createdAt, eh.updated_at as updatedAt from T_ENT_HIST eh left outer join T_TERMINAL tm on eh.terminal_cd = tm.terminal_cd LEFT OUTER JOIN T_CMMN_CD c1 on c1.cd = eh.ent_evt_typ and c1.cd_typ = 'EntEvtTyp' LEFT OUTER JOIN T_CMMN_CD c2 on c2.cd = eh.card_class_typ and c2.cd_typ = 'CardClassTyp' LEFT OUTER JOIN T_CMMN_CD c3 on c3.cd = eh.card_state_typ and c3.cd_typ = 'CardStateTyp' LEFT OUTER JOIN T_CMMN_CD c4 on c4.cd = eh.card_tag_typ and c4.cd_typ = 'CardTagTyp' LEFT OUTER JOIN T_CMMN_CD c5 on c5.cd = eh.auth_way_typ and c5.cd_typ = 'AuthWayTyp' where 1=1
AND eh.ent_evt_typ = #{srchCond1}
AND eh.evt_dt BETWEEN #{fromDt} AND #{toDt}
AND eh.building_id = #{srchCond2}
and (eh.emp_nm LIKE CONCAT('%',#{keyword},'%') or eh.emp_cd like CONCAT('%',#{keyword},'%') )
ORDER BY evtDt DESC limit #{srchCnt} offset #{offset}

select
   count(*)
from	T_ENT_HIST
where
    1=1

    and ent_evt_typ = #{srchCond1}


    and evt_dt between #{fromDt} and #{toDt}


    and building_id = #{srchCond2}


    and (emp_nm LIKE CONCAT('%',#{keyword},'%') or emp_cd like CONCAT('%',#{keyword},'%') )

SELECT evt_dt AS evtDt, door_alarm_typ AS doorAlarmTyp, (SELECT cd_nm from T_CMMN_CD WHERE cd_typ = 'DoorAlarmTyp' AND cd = door_alarm_typ) AS doorAlarmTypNm, terminal_id AS terminalId, terminal_cd AS terminalCd, terminal_typ AS terminalTyp, ip_addr AS ipAddr, model_nm AS modelNm, mgmt_num AS mgmtNum, complex_auth_typ AS complexAuthTyp, (SELECT cd_nm from T_CMMN_CD WHERE cd_typ = 'ComplexAuthTyp' AND cd = complexAuthTyp) AS complexAuthTypNm, door_id AS doorId, door_nm AS doorNm, building_id AS buildingId, building_nm AS buildingNm, created_at AS createdAt FROM T_DOORALARM_HIST where 1=1
and door_alarm_typ = #{srchCond}
and evt_dt between #{fromDt} and #{toDt}
and door_nm like CONCAT('%',#{keyword},'%')
limit #{srchCnt} offset #{offset}

select count(*) from T_DOORALARM_HIST where 1=1
and door_alarm_typ = #{srchCond}
and evt_dt between #{fromDt} and #{toDt}
and door_nm like CONCAT('%',#{keyword},'%')
