mapper.select:
SELECT id, evt_dt AS evtDt, ent_evt_typ AS entEvtTyp, ( SELECT cd_nm FROM T_CMMN_CD WHERE cd_typ = 'EntEvtTyp' AND cd = ent_evt_typ ) AS entEvtTypNm, terminal_cd AS terminalCd, model_nm AS modelNm, mgmt_num AS mgmtNum, ip_addr AS ipAddr, complex_auth_typ AS complexAuthTyp, door_nm AS doorNm, building_nm AS buildingNm, emp_nm AS empNm, emp_no AS empNo, dept_cd AS deptCd, dept_nm AS deptNm, belong_nm AS belongNm FROM T_ENT_HIST WHERE 1=1
AND ent_evt_typ = #{srchCond1}
AND evt_dt BETWEEN #{fromDt} AND #{toDt}
AND building_id = #{srchCond2}
AND emp_nm LIKE CONCAT('%',#{keyword},'%')
limit #{srchCnt} offset #{offset}

select
   count(*)
from	T_ENT_HIST
where
    1=1

    and ent_evt_typ = #{srchCond1}


    and evt_dt between #{fromDt} and #{toDt}


    and building_id = #{srchCond2}


    and emp_nm like CONCAT('%',#{keyword},'%')

SELECT evt_dt AS evtDt, door_alarm_typ AS doorAlarmTyp, (SELECT cd_nm from T_CMMN_CD WHERE cd_typ = 'DoorAlarmTyp' AND cd = door_alarm_typ) AS doorAlarmTypNm, terminal_id AS terminalId, terminal_cd AS terminalCd, terminal_typ AS terminalTyp, ip_addr AS ipAddr, model_nm AS modelNm, mgmt_num AS mgmtNum, complex_auth_typ AS complexAuthTyp, (SELECT cd_nm from T_CMMN_CD WHERE cd_typ = 'ComplexAuthTyp' AND cd = complexAuthTyp) AS complexAuthTypNm, door_id AS doorId, door_nm AS doorNm, building_id AS buildingId, building_nm AS buildingNm, created_at AS createdAt FROM T_DOORALARM_HIST where 1=1
and door_alarm_typ = #{srchCond}
and evt_dt between #{fromDt} and #{toDt}
and door_nm like CONCAT('%',#{keyword},'%')
limit #{srchCnt} offset #{offset}

select count(*) from T_DOORALARM_HIST where 1=1
and door_alarm_typ = #{srchCond}
and evt_dt between #{fromDt} and #{toDt}
and door_nm like CONCAT('%',#{keyword},'%')
