mapper.insert:
		INSERT
		  INTO T_USER
              ( login_id
              , login_pwd
              , user_nm)
        VALUES( #{login_id}
              , #{login_pwd}
              , #{user_nm}
              )

mapper.select:
select	COUNT(1) as cnt
from	T_USER
where  login_id = #{login_id}
  and	login_pwd = #{login_pwd}

mapper.update:
update  T_USER
set 	login_pwd = #{login_pwd}
where	login_id = #{login_id}

mapper.select:
select	COUNT(1) as cnt
from	T_USER
where  login_id = #{login_id}
  and	login_pwd = #{login_pwd}
