mapper.select:
SELECT A.id , A.nm , A.door_sch_id , A.door_sch_nm , DATE_FORMAT(A.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(A.updated_at, '%Y-%m-%d') as updated_at , A.door_cnt FROM ( SELECT TD.id , TD.nm , TD.door_sch_id , TDC.door_sch_nm , TD.created_at , TD.updated_at , (select COUNT(TDD.door_id) FROM T_DOORGRP_DOOR TDD where TDD.doorgrp_id = TD.id )AS door_cnt FROM T_DOORGRP TD LEFT JOIN T_DOOR_SCH TDC on TD.door_sch_id = TDC.id WHERE 1=1
AND TD.id = #{id}
AND TD.nm like CONCAT('%',#{keyword},'%')
) A ORDER BY A.created_at DESC
LIMIT #{srchCnt} OFFSET #{offset}

SELECT COUNT(*) FROM ( SELECT TD.id , TD.nm , TD.door_sch_id , TDC.door_sch_nm , DATE_FORMAT(TD.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(TD.updated_at, '%Y-%m-%d') as updated_at FROM T_DOORGRP TD LEFT JOIN T_DOOR_SCH TDC on TD.door_sch_id = TDC.id WHERE 1=1
AND TD.id = #{id}
AND TD.nm like CONCAT('%',#{keyword},'%')
) A

mapper.insert:
INSERT
INTO T_DOORGRP
( id
, nm
, door_sch_id)
