mapper.select:
SELECT A.id , A.door_sch_nm , A.created_at , A.updated_at FROM( SELECT TDS.id , TDS.door_sch_nm , DATE_FORMAT(TDS.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(TDS.updated_at, '%Y-%m-%d') as updated_at FROM T_DOOR_SCH TDS WHERE 1=1
AND TDS.id = #{id}
AND TDS.door_sch_nm like CONCAT('%',#{keyword},'%')
) A ORDER BY A.created_at DESC
LIMIT #{srchCnt} OFFSET #{offset}

SELECT COUNT(A.id) FROM ( SELECT TDS.id, TDS.door_sch_nm, , DATE_FORMAT(TDS.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(TDS.updated_at, '%Y-%m-%d') as updated_at FROM T_DOOR_SCH TDS WHERE 1=1
AND TDS.id = #{id}
AND TDS.door_sch_nm like CONCAT('%',#{keyword},'%')
) A

mapper.insert:
INSERT
INTO T_DOOR_SCH
( id,
  door_sch_nm,
  created_at,
  updated_at
)
VALUES
    ( #{id}
    , #{doorSchNm}
    , NOW()
    , NOW()
    )

mapper.update:
UPDATE T_DOOR_SCH
SET door_sch_nm = #{doorSchNm}
  , updated_at= NOW()
WHERE id = #{id}

mapper.insert:
INSERT
INTO T_DOOR_SCH_WEEKDAY
( id,
  door_sch_id,
  weekday,
  beg_tm,
  end_tm,
  created_at,
  updated_at
)
VALUES
    ( #{id}
    , #{doorSchId}
    , #{weekday}
    , #{begTm}
    , #{endTm}
    , NOW()
    , NOW()
    )

mapper.update:
UPDATE T_DOOR_SCH_WEEKDAY
SET door_sch_id = #{doorSchId}
  , weekday= #{weekday}
  , beg_tm= #{begTm}
  , end_tm= #{endTm}
  , updated_at= NOW()
WHERE id = #{id}
